mapper.select:
select *
	from
	(
		select rownum as rnum, A.*
		from
		(
			select * from address_book
			where emp_no=#{empNo}

	   	 		and	${searchCondition} LIKE '%'|| #{searchKeyword} ||'%'

		)A
	)

where rnum > #{firstRecordIndex}
and rnum <=  #{firstRecordIndex}+ #{recordCountPerPage}

		select count(*) from address_book
		where emp_no=#{empNo}

  	 		and	${searchCondition} LIKE '%'|| #{searchKeyword} ||'%'
