mapper.update:
update board
set readcount=readcount+1
where board_no=#{boardNo}

update board
set title=#{title}, content=#{content}
where boardList_no=1000 and board_No=#{boardNo}

update board
set title=#{title}, content=#{content}, regdate=sysdate
where boardList_no=2000 and board_No=#{boardNo}

mapper.delete:
update board
set delflag='Y'
where boardList_no=2000 and board_no=#{boardNo}

mapper.select:
select * from boardListView
where emp_no=#{empNo}
order by boardList_no asc, board_no desc
