mapper.select:
select *
from
(
	select rownum as rnum, A.*
	from
	(
		select * from qnaListView
		
			where ${searchCondition} like '%' || #{searchKeyword} ||'%'
		
		order by board_no desc
	)A
)

	where rnum>#{firstRecordIndex}
	and rnum<=#{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update board
set readcount=readcount+1
where board_no=#{boardNo}

update board
set title=#{title}, content=#{content}
where boardList_no=1000 and board_No=#{boardNo}

update board
set title=#{title}, content=#{content}
where boardList_no=2000 and board_No=#{boardNo}

mapper.delete:
update board
set delflag='Y'
where boardList_no=2000 and board_no=#{boardNo}

mapper.select:
	select *
	from
	(
		select rownum as rnum, A.*
		from
		(
			select * from boardListView
			where emp_no=#{empNo}
			
			and board_name='${searchCondition}'
			
			
      		and content like '%'|| #{searchKeyword} || '%'
      		
			and delflag='N'
			order by regdate desc
		)A
	)
	
	where rnum > #{firstRecordIndex}
	and rnum <=  #{firstRecordIndex}+ #{recordCountPerPage}

select * from ( select title,max(board_no) from noticeListView
where board_no<#{boardNo}
group by title order by max(board_no) desc ) where rownum=1

select * from ( select title,min(board_no) from noticelistview
where board_no>#{boardNo}
group by title order by min(board_no) asc ) where rownum=1

mapper.update:
update board
set title=#{title}, content=#{content}
where boardList_no=4000 and board_no=#{boardNo}

update board
set delflag='Y'
where boardList_no=4000 and board_no=#{boardNo}

update board
set title=#{title}, content=#{content}
where boardList_no=5000
and board_no=#{boardNo}

update board
set delflag='Y'
where boardList_no=5000 and board_no=#{boardNo}
