mapper.select:
select * from CLUBBOARDVIEW where CLUB_NO=#{clubNo}
where ${searchVo.searchCondition} like '%' || #{searchVo.searchKeyword} ||'%'
order by regDate desc

mapper.update:
update clubBoard
set readcount=readcount+1
where board_no=#{boardNo}

update clubBoard
set TITLE=#{title}, CONTENT=#{content}
where board_no=#{boardNo} and CLUB_NO=#{clubNo}
