mapper.select:
select count(*)
from commute
where work_in like '%' || to_char(sysdate, 'yyyy-mm-dd') || '%' and emp_no = #{empNo}

select count(*)
from commute
where work_out like '%' || to_char(sysdate, 'yyyy-mm-dd') || '%' and emp_no = #{empNo}

select commute_state from commute 
WHERE emp_no = #{empNo}
AND TO_DATE(work_in, 'YYYY-MM-DD HH24:MI:SS') >= TRUNC(SYSDATE)

select count(*) from commute
where commute_state = 1
and emp_no = #{empNo}
and substr(work_in, 1, 7) = #{date}

select count(*) from commute
where commute_state = 2
and emp_no = #{empNo}
and substr(work_in, 1, 7) = #{date}

select count(*) from commute
where emp_no = #{empNo}
and substr(work_in, 1, 7) = #{date}

select * from commute
where emp_no=#{empNo}
and substr(work_in, 1, 7) = #{date}
