mapper.select:
    SELECT *
    FROM (
        SELECT ROWNUM AS RNUM, A.*
        FROM (
            SELECT *
            FROM secondmarketview
            
                
                    
                        where (title LIKE '%' || #{searchKeyword} || '%' OR NAME LIKE '%' || #{searchkeyword} || '%' OR discription LIKE '%' || #{searchKeyword} || '%')
                    
                    
                        where ${searchCondition} LIKE '%' || #{searchKeyword} || '%'
                    
                
            
            ORDER BY trade_no DESC
        ) A
    ) 
	    
	    WHERE RNUM > #{firstRecordIndex} 
	    AND RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update secondhand_trade
set title = #{title}, product = #{product}, price= #{price}, discription=#{discription}

,selflag=#{selFlag}

where trade_no=#{tradeNo}

update secondhand_trade
set likecount = likecount + 1
where trade_no = #{tradeNo}
